import phones from 'api/mockPhones';
import categories from 'api/mockCategories';

//@ загрузить все телефоны
export const fetchPhones = async () => {
    return new Promise((resolve, reject) => {
        resolve(phones);
    });
}

//@ загрузить еще телефонов (пейджинг)
export const loadMorePhones = async ({ offset }) => { //тут offset - передаем в объекте и делаем диструктуризацию т.к. это типа проще , если вдруг потом будет много параметров, то их черз запятую неудобно передавать...
    return new Promise((resolve, reject) => {
        resolve(phones);
    });
}

//@ получить телефон по id
export const fetchPhoneById = async (id) => {
    return new Promise((resolve, reject) => {
        let result = phones.find(x => x.id === id);
        resolve(result);
    });
}


//@ загрузить все категории
export const fetchCategories = async () => {
    return new Promise((resolve, reject) => {
        resolve(categories);
    });
}