import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
//--
import { getTotalBasketCount, getTotalBasketPrice } from 'selectors';

const BasketCart = (props) => {

    const { totalBasketCount, totalPrice } = props;

    return (
        <div className="cart">
            <div className="dropdown">
                <Link to='/basket' id='dLabel' className='btn btn-inverse btn-block, btn-lg' >
                    <i className='fa fa-fa-fa-shopping-cart' />
                    <span>{totalBasketCount} items ({totalPrice}$)</span>
                </Link>
            </div>
        </div>
    );
}

const mapStateToProps = state => {
    return {
        totalBasketCount: getTotalBasketCount(state), //это методы описанные в selectors.js
        totalPrice: getTotalBasketPrice(state)
    }
}

export default connect(mapStateToProps)(BasketCart);