import React from 'react'
import {connect} from 'react-redux'
import {getTotalBasketPrice, getBasketPhonesWithCount} from 'selectors'
import {removePhoneFromBasket} from 'actions'

const Basket = (props) => {
  const {phones, totalPrice, removePhoneFromBasket} = props

  const renderSidebar = () => {
    return (
      <div>SIDEBAR</div>
    )
  }

  const isBasketEmpty = phones.length <= 0

  const renderContent = () => {
    return (
      <div>
        {isBasketEmpty && <div>Your shopping cart is empty</div>}

        <div className='table-responsive'>
          <table className='table-bordered table-striped table-condensed cf'>
            <tbody>
            {phones.map((phone, index) => (
              <tr
                key={index}
                className='item-checout'
              >
                <td className='first-column-checkout'>
                  <img
                    className='img-thumbnail'
                    src={phone.image}
                    alt={phone.name}
                  />
                </td>
                <td>{phone.name}</td>
                <td>${phone.price}</td>
                <td>{phone.count}</td>
                <td>
                  <span className='delete-cart' onClick={()=>removePhoneFromBasket(phone.id)}></span>
                </td>
              </tr>
            ))}
            </tbody>
          </table>
        </div>
        {
          !isBasketEmpty &&
          <div className='row'>
            <div className='pull-right total-user-checkout'>
              <b>Total:</b>
              ${totalPrice}
            </div>
          </div>
        }
      </div>
    )
  }

  //выводим два варинта вьюхи: когда карзина пустая, и когда карзина не пустая
  return (
    <div className='view-container'>
      <div className="container">
        <div className="row">
          <div className="col-md-9">
            {renderContent()}
          </div>
          <div className="col-md-3 btn-user-checkout">
            {renderSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    phones: getBasketPhonesWithCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

const mapDispatchToProps = {
  removePhoneFromBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)