import * as R from 'ramda'
import * as types from 'actionTypes'

const initialState = []  //ЭТО БУДЕТ МАССИВ С ID ТЕЛЕФОНОВ

export default (state = initialState, action) => {
  const {type, payload} = action

  switch (type) {
    case types.ADD_PHONE_TO_BASKET:
      return R.append(payload, state) //т.к. стейт у нас массив мы просто добавляем в массив_state  - новый id телефона (рамбда иммутабельна поэтому это будет новый массив)
    case types.REMOVE_PHONE_FROM_BASKET:
      return state.filter(x => x != payload)
    default:
      return state
  }
}