import * as R from 'ramda';
import * as types from 'actionTypes';

const initialState = {
    id: null
};

export default (state = initialState, action) => {
    const { type, payload } = action;

    switch (type) {
        case types.FETCH_PHONE_BY_ID_SUCCESS:

            return R.merge(state, {
                id: payload.id 
            })

        default:
            return state;
    }
}